mapper.select:
    
        SELECT 
			STYLISTID,
			STYLISTNAME
        FROM MSTSTYLIST
        WHERE (
            STYLISTID NOT IN (
                SELECT STYLISTID
                FROM TRNSTYLISTSCHEDULE
                WHERE OFFDATE = #{date}
            )
        ) AND (
            STYLISTID NOT IN (
                SELECT 
					STYLISTID
                FROM 
					TRNRESERVATION
                WHERE
					RESERVATIONDATE = #{time}
				OR
					RESERVATIONENDDATE >= #{time}
				AND
					RESERVATIONENDDATE <= #{endTime}
            )
        ) AND 
			STYLISTSTATUS = 0;
